mapper.update:
UPDATE sys_student_whitelist
SET
dept_id = #{deptId},
major = #{major},
import_batch = #{importBatch}
WHERE student_id = #{studentId}

mapper.select:
SELECT id, student_id, dept_id, major
FROM sys_student_whitelist
WHERE student_id = #{studentId}

SELECT COUNT(1) > 0
FROM sys_student_whitelist
WHERE student_id = #{studentId}
